.events(onClick).sample(selectedOptionVar.signal)", "          val responseStream = clickStream.flatMapSwitch { opt =>", "            AjaxStream", "              .get(", "                url = opt.url,", "                // These observers are optional, we're just using them for demo", "                requestObserver = pendingRequestVar.someWriter,", "                progressObserver = eventsVar.updater { (evs, p) =>", "                  val ev = p._2", "                  evs :+ s\"Progress: ${ev.loaded} / ${ev.total} (lengthComputable = ${ev.lengthComputable})\"", "                },", "                readyStateChangeObserver = eventsVar.updater { (evs, req) =>", "                  evs :+ s\"Ready state: ${req.readyState}\"", "                }", "              )", "              .map(\"Response: \" + _.responseText)", "              .recover { case err: AjaxStreamError => Some(err.getMessage) }", "          }", "", "          List(", "            clickStream.map(opt => List(s\"Starting: GET ${opt.url}\")) --> eventsVar,", "            responseStream --> eventsVar.updater[String](_ :+ _)", "          )", "        }", "      ),", "      \" \",", "      button(", "        typ(\"button\"),", "        \"Abort\",", "        onClick --> (_ => pendingRequestVar.now().foreach(_.abort()))", "      )", "    ),", "    div(", "      fontSize.em(0.8),", "      br(),", "      b(\"Events:\"),", "      div(children <-- eventsVar.signal.map(_.map(div(_))))", "    )", "  )", "}")),
