  val `wind-gradient` = List(
    CodeSnippet("./shared/src/main/scala/com/raquo/weather/GradientReport.scala", "GradientReport.scala", "scala".asInstanceOf[LanguageName], 6, 55, "wind-gradient", List("import io.bullet.borer.*", "import io.bullet.borer.derivation.MapBasedCodecs.*", "", "/**", "  *", "  * @param cities                  List of cities to display, in order", "  * @param currentConditionsByCity cityId -> currentConditions.", "  *                                Note: some records might be missing if current conditions are not available", "  * @param forecastDays            list of day captions, in order to be displayed", "  * @param forecastsByDay          (day_caption -> cityId -> forecast)", "  */", "@JSExportAll", "case class GradientReport(", "  cities: List[CityStation],", "  currentConditionsByCity: Map[String, CityCurrentConditions],", "  forecastDays: List[String],", "  forecastsByDay: Map[String, Map[String, CityForecast]]", ") {", "", "  def selectCurrentConditions[A](", "    f: CityCurrentConditions => Option[A]", "  ): List[Option[A]] = {", "    cities.flatMap { city =>", "      for {", "        conditions <- currentConditionsByCity.get(city.id)", "      } yield {", "        f(conditions)", "      }", "    }", "  }", "", "  def selectForecasts[A](", "    day: String,", "    f: CityForecast => Option[A]", "  ): List[Option[A]] = {", "    cities.flatMap { city =>", "      for {", "        forecastsByCity <- forecastsByDay.get(day)", "        forecast <- forecastsByCity.get(city.id)", "      } yield {", "        f(forecast)", "      }", "    }", "  }", "}", "", "object GradientReport {", "", "  given codec: Codec[GradientReport] = deriveCodec", "}")),
    CodeSnippet("./shared/src/main/scala/com/raquo/weather/Gradient.scala", "Gradient.scala", "scala".asInstanceOf[LanguageName], 8, 44, "wind-gradient", List("@JSExportAll", "enum Gradient(", "  val id: String,", "  val name: String,", "  val cities: List[CityStation]", ") {", "", "  val cityIds: List[String] = cities.map(_.id)", "", "  // Note: Lytton burned down and does not have current observations, so its usefulness is questionable", "  case Squamish extends Gradient(", "    id = \"squamish\",", "    name = \"Squamish\",", "    cities = List(Kamloops, Lytton, Merritt, Hope, Abbotsford, Vancouver, CityStation.Squamish, Whistler, Pemberton, Lillooet)", "  )", "", "  case Nitinat extends Gradient(", "    id = \"nitinat\",", "    name = \"Nitinat\",", "    cities = List(Ucluelet, PortAlberni)", "  )", "}", "", "object Gradient {", "", "  //given codec: Codec[Gradient] = Codec.of[String].bimap(_.id, forId(_))", "", "  val gradientIds: List[String] = Gradient.values.map(_.id).toList", "", "  def forId(gradientId: String): Option[Gradient] = {", "    Gradient.values.find(_.id == gradientId)", "  }", "", "  def forIdGet(gradientId: String): Gradient = {", "    forId(gradientId).getOrElse(throw new Exception(s\"Unknown gradient id: `${gradientId}`\"))", "  }", "}")),
    CodeSnippet("./client/src/main/scala/com/raquo/app/weather/WeatherGradientChart.scala", "WeatherGradientChart.scala", "scala".asInstanceOf[LanguageName], 12, 227, "wind-gradient", List("object WeatherGradientChart {", "", "  def makeChart(", "    canvasElement: dom.html.Canvas", "  ): Chart = {", "    // Note: most data and configuration is updated when the graph data is received", "", "    val chart = new Chart(", "      canvas = canvasElement,", "      config = new ChartConfig(", "        data = new ChartData(),", "        options = new ChartConfigOptions(", "          animation = false,", "          plugins = js.Dictionary(", "            \"legend\" -> js.Dynamic.literal(", "              \"display\" -> true,", "              \"position\" -> \"bottom\",", "              \"labels\" -> js.Dynamic.literal(", "                \"boxHeight\" -> 0,", "              )", "            ),", "            \"datalabels\" -> js.Dynamic.literal(", "              \"font\" -> js.Dynamic.literal(", "                \"weight\" -> \"bold\"", "              )", "            )", "          ),", "          // This makes the chart respond to match the size of its", "          // parent container (-graphbox in our case).", "          // See https://www.chartjs.org/docs/latest/configuration/responsive.html", "          maintainAspectRatio = false", "        )", "      )", "    )", "", "    chart", "  }", "", "  def updateChart(chart: Chart, selectedDay: Option[String], report: GradientReport): Unit = {", "    val cityNames = report.cities.toJSArray.map(_.name)", "", "    val (temperatures, pressures, datasets) = selectedDay match {", "      case None =>", "        // Current weather", "        val temperatures = report.selectCurrentConditions(_.temperatureC)", "        val pressures = report.selectCurrentConditions(_.pressureKPa)", "        (", "          temperatures.flatten,", "          pressures.flatten,", "          js.Array(", "            temperatureDatasetConfig(temperatures.map(_.orNull).toJSArray),", "            pressureDatasetConfig(pressures.map(_.orNull).toJSArray)", "          )", "        )", "      case Some(forecastDay) =>", "        val temperatures = report.selectForecasts(forecastDay, _.temperatureC.some)", "        val datasets = js.Array(", "          temperatureDatasetConfig(temperatures.map(_.orNull).toJSArray)", "        )", "        (temperatures.flatten, Nil, datasets)", "    }", "", "    val axes = scalesConfig(temperatures, pressures)", "", "    // To update chart.js graph, you need to mutate its config,", "    // then call chart.update() for it to take effect.", "    chart.config.data.labels = cityNames", "    chart.config.data.datasets = datasets", "    chart.config.options.scales = axes", "    chart.update()", "  }", "", "  private def temperatureDatasetConfig(temperatures: js.Array[Double | Null]): ChartDataset = {", "    new ChartDataset(", "      label = \"Temperature\",", "      typ = \"line\",", "      yAxisID = \"yMain\",", "      data = temperatures,", "      borderColor = \"#0000f0\",", "      pointBackgroundColor = \"#0000d0\",", "      pointBorderWidth = 0,", "      pointRadius = 4,", "      pointHoverRadius = 5,", "      pointHoverBorderWidth = 0", "    ).updateDynamic(", "      \"spanGaps\" -> true,", "      \"datalabels\" -> js.Dynamic.literal(", "        \"align\" -> \"top\",", "        \"color\" -> \"#000080\",", "        \"formatter\" -> (((value: Double, ctx: js.Dynamic) => {", "          Math.round(value).toString + \"°\"", "        }): js.Function2[Double, js.Dynamic, Any])", "      )", "    )", "  }", "", "  private def pressureDatasetConfig(pressures: js.Array[Double | Null]): ChartDataset = {", "    new ChartDataset(", "      label = \"Pressure\",", "      typ = \"line\",", "      yAxisID = \"yPressure\",", "      data = pressures,", "      borderColor = \"#f00000\",", "      pointBackgroundColor = \"#d00000\",", "      pointBorderWidth = 0,", "      pointRadius = 4,", "      pointHoverRadius = 5,", "      pointHoverBorderWidth = 0", "    ).updateDynamic(", "      \"spanGaps\" -> true,", "      \"datalabels\" -> js.Dynamic.literal(", "        \"align\" -> \"top\",", "        \"color\" -> \"#800000\"", "      )", "    )", "  }", "", "  private def scalesConfig(", "    temperatures: List[Double],", "    pressures: List[Double]", "  ): js.Dictionary[ChartAxis] = {", "    val xAxis = new ChartAxis()", "      .updateDynamic(", "        \"type\" -> \"category\",", "        \"grid\" -> js.Dictionary(", "          \"display\" -> false", "        ),", "        \"ticks\" -> js.Dynamic.literal(", "          \"minRotation\" -> 90,", "          \"maxRotation\" -> 90,", "        ),", "        \"offset\" -> true", "      )", "    val scales = js.Dictionary(", "      \"x\" -> xAxis,", "      \"yMain\" -> yMainScale(temperatures)", "    )", "", "    yPressureScale(pressures).foreach { scale =>", "      scales.update(\"yPressure\", scale)", "    }", "", "    scales", "  }", "", "  private def yMainScale(temperatures: List[Double]): ChartAxis = {", "    val stepSize = 5", "    val range = fixedScaleRange(", "      step = stepSize,", "      desiredNumSteps = 7,", "      dataValues = temperatures,", "      forceMin = if (temperatures.forall(_ > 0)) Some(0) else None", "    )", "    new ChartAxis(", "      position = \"left\",", "      min = range.map(_._1).orUndefined,", "      max = range.map(_._2).orUndefined", "    ).updateDynamic(", "      \"ticks\" -> js.Dictionary(", "        //\"count\" -> 7,", "        \"stepSize\" -> stepSize", "      ),", "      \"grid\" -> js.Dictionary(", "        \"display\" -> false", "      )", "    )", "  }", "", "  private def yPressureScale(pressures: List[Double]): Option[ChartAxis] = {", "    val pressureStepSize = 0.1", "    fixedScaleRange(", "      step = pressureStepSize,", "      desiredNumSteps = 8,", "      dataValues = pressures", "    ).map { (min, max) =>", "      new ChartAxis(", "        position = \"right\",", "        min = min,", "        max = max + pressureStepSize // allow extra space for data point labels", "      ).updateDynamic(", "        \"ticks\" -> js.Dictionary(", "          \"stepSize\" -> pressureStepSize,", "          \"callback\" -> (((value: Double) => f\"$value%.1f\"): js.Function1[Double, String])", "        ),", "        \"grid\" -> js.Dictionary(", "          \"display\" -> false", "        )", "      )", "    }", "  }", "", "  /** We want vertical axes to have consistent range (max - min value),", "    * so that it's easier for the user to judge the difference between", "    * the values for different cities on the chart. We also want to", "    * snap the min and max to certain steps.", "    * */", "  private def fixedScaleRange(", "    step: Double,", "    desiredNumSteps: Int,", "    dataValues: List[Double],", "    forceMin: Option[Double] = None", "  ): Option[(Double, Double)] = {", "    for {", "      minValue <- dataValues.minOption", "      maxValue <- dataValues.maxOption", "    } yield {", "      val desiredRange = desiredNumSteps * step", "      val scaleMin = forceMin.getOrElse {", "        val mean = (minValue + maxValue) / 2", "        Math.floor((mean - desiredRange / 2) / step) * step", "      }", "      val scaleMax = scaleMin + desiredRange", "      (scaleMin, scaleMax)", "    }", "  }", "}")),
    CodeSnippet("./client/src/main/scala/com/raquo/app/weather/WeatherGradientView.less", "WeatherGradientView.less", "less".asInstanceOf[LanguageName], 2, 28, "wind-gradient", List(".WeatherGradientView {", "", "  --chartHeight: 400px;", "", "  @media(max-width: 800px) {", "    --chartHeight: min(100vh, 100vw);", "  }", "", "  .Tabs {", "    margin: 20px 0;", "  }", "", "  .-loading {", "    display: flex;", "    align-items: center;", "    justify-content: center;", "    height: var(--chartHeight);", "    background: #f5f5f5;", "    color: #333;", "    font-size: 0.9em;", "  }", "", "  .-graphbox {", "    position: relative;", "    height: var(--chartHeight);", "  }", "}")),
    CodeSnippet("./client/src/main/scala/com/raquo/app/weather/WeatherGradientView.scala", "WeatherGradientView.scala", "scala".asInstanceOf[LanguageName], 19, 195, "wind-gradient", List("object WeatherGradientView {", "", "  // Find and import the LESS (CSS) file for this component.", "  // See https://github.com/raquo/vite-plugin-glob-resolver", "  // See https://github.com/raquo/vite-plugin-import-side-effect", "  @JSImport(\"@find/**/WeatherGradientView.less\", JSImport.Namespace)", "  @js.native private object Stylesheet extends js.Object", "", "  useImport(Stylesheet)", "", "  // Chart.js requires you to load the necessary components this way", "  // to avoid loading the entire library. It will also tell you which", "  // components are needed if you forget to include them.", "  Chart.register(", "    ChartDataLabelsPlugin,", "    Legend,", "    //BarController,", "    //BarElement,", "    LineController,", "    LineElement,", "    PointElement,", "    CategoryScale,", "    LinearScale,", "  )", "", "  def apply(pageS: Signal[WeatherGradientPage]): HtmlElement = {", "", "    // Just a naming convention: if a variable ends in a capital S, it's a Signal or Stream.", "    // Helps to differentiate Scala collections from observables, and their mixes:", "    // e.g. users: List[User], userS: Signal[User], usersS: Signal[List[User]]", "    val gradientS = pageS.flatMapSwitch { p =>", "      Gradient.forId(p.gradientId) match {", "        case Some(gradient) =>", "          EventStream.fromValue(gradient)", "        case None =>", "          js.timers.setTimeout(0) {", "            // #TODO the delay is needed in case when you're serving the production application,", "            //  and the user navigates to a bad gradient wind URL via the address bar (as opposed", "            //  to using waypoint / pushState). Not quire sure why, must be some race condition.", "            //  Ideally Waypoint should have better built-in support for async route validation.", "            //  Actually it would be good to show how to do both sync and async route validation.", "            forcePage(NotFoundPage)", "          }", "          EventStream.empty", "      }", "    }", "", "    val apiResponseS = gradientS.flatMapSwitch { gradient =>", "      FetchStream", "        .withDecoder(jsonApiDecoder[GradientReport])", "        .get(absRoot / \"api\" / \"weather\" / \"gradient\" / gradient.id)", "    }", "    // collect-ing the stream simply skips all events that don't match", "    // (errors, in this case). Note: you can't `filter` or `collect`", "    // SIGNALS because they must always have a current value.", "    // Read Airstream docs to understand why, and how to do what you need.", "    val gradientReportStream = apiResponseS.collect {", "      case ApiResponse.Result(report) => report", "    }", "", "    div(", "      // attr(value) is alias for attr := value", "      // `cls` is alias for `className` – this HTML DOM property sets CSS class name on this element", "      cls(\"WeatherGradientView\"),", "      h1(", "        text <-- gradientS.map(_.name + \" gradient\")", "      ),", "      renderOtherGradients(gradientS),", "      renderError(apiResponseS),", "      child <-- gradientReportStream.toWeakSignal.splitOption(", "        (initialReport, reportSignal) => renderChart(initialReport, reportSignal),", "        ifEmpty = div(cls(\"-loading\"), \"Fetching data...\")", "      ),", "      CodeSnippets(", "        _.`wind-gradient`.sortBy(_.fileName != \"WeatherGradientView.scala\"),", "        startExpanded = _.fileName == \"WeatherGradientView.scala\"", "      )", "    )", "  }", "", "  private def renderOtherGradients(", "    gradientS: EventStream[Gradient]", "  ): Modifier.Base = {", "    // We use implicit conversion here to convert a list of modifiers into one modifier", "    List[Modifier.Base](", "      \"See also: \",", "      children <-- gradientS.map { gradient =>", "        val otherGradients = Gradient.values.filterNot(_ == gradient)", "        otherGradients.flatMap { g =>", "          List[Node]( // Node is the common type of elements and text nodes", "            a(g.name, navigateTo(WeatherGradientPage(g.id))),", "            \", \"", "          )", "        }.toList.init", "      }", "    )", "  }", "", "  // This function is only called once (when gradientReportStream emits", "  // the network response), so all the elements that it creates are created", "  // only once. Remember – no virtual DOM in Laminar.", "  // Then, we use lifecycle hooks and --> methods to bring reactivity to", "  // those elements that we created, so that, for example, when you click", "  // on a tab for a different day, we don't re-render the whole chart from", "  // scratch, but rather we update it with new data using Chart.js update API.", "  private def renderChart(", "    initialReport: GradientReport,", "    gradientReportS: Signal[GradientReport]", "  ): HtmlElement = {", "    val selectedDayVar = Var(Option.empty[String]) // \"None\" means \"Current observations\"", "    var maybeChart = Option.empty[Chart]", "    div(", "      Tabs(", "        initialReport.forecastDays,", "        maybeForecastDay => {", "          List(", "            // Setting class name dynamically", "            cls(\"x-selected\") <-- selectedDayVar.signal.map(_ == maybeForecastDay),", "            // When this tab button is clicked, send the corresponding day into the var", "            onClick.mapTo(maybeForecastDay) --> selectedDayVar", "          )", "        }", "      ),", "      div(", "        // Don't put anything else in this div except for the canvas tag!", "        // That's a Chart.js requirement for responsive sizing.", "        cls(\"-graphbox\"),", "        canvasTag(", "          // This lifecycle block instantiates the chart when this element is mounted,", "          // and frees up its resources when it's unmounted. This way you can navigate", "          // in and out of the graph page without using up more resources than needed.", "          onMountUnmountCallback(", "            mount = ctx => {", "              maybeChart = Some(WeatherGradientChart.makeChart(ctx.thisNode.ref))", "            },", "            unmount = _ => maybeChart.foreach(_.destroy())", "          ),", "          // The callback below is invoked whenever you select a different date, or whenever", "          // we get new data from the server (the latter only happens once in this application).", "          // As you see we are not re-creating the chart here on every update, rather,", "          // we reuse the existing chart, pushing updated config to it. This is much more", "          // efficient and gives users a better experience.", "          //", "          // Note: mounting (see onMountUnmountCallback above) is synchronous, and the network", "          // request to fetch graph data is asynchronous, so the latter is guaranteed to happen", "          // AFTER this mounting callback. In this callback we implicitly rely on this sequence", "          // of events, because if it was the other way round, maybeChart here would be empty,", "          // and this callback wouldn't do anything. You always need to consider such things", "          // when building UI applications, but thankfully JS world is single threaded, so it's", "          // much easier to reason about.", "          selectedDayVar.signal.combineWith(gradientReportS) --> { (selectedDay, report) =>", "            maybeChart.foreach { chart =>", "              WeatherGradientChart.updateChart(chart, selectedDay, report)", "            }", "          }", "        ),", "      )", "    )", "  }", "", "", "", "  // Your render functions don't need to return just elements, for example", "  // this one returns a Modifier that inserts a dynamic child node wherever", "  // it's put.", "  private def renderError(apiResponseS: EventStream[ApiResponse[?]]): Mod[Element] = {", "    val maybeApiErrorS = apiResponseS.collect {", "      case ApiResponse.Error(msg, _) => Some(msg)", "      case _ => None", "    }", "    child.maybe <-- maybeApiErrorS.map(_.map { err =>", "      div(textAlign.center, span(cls(\"u-error\"), err))", "    })", "  }", "", "", "}")),
